head.script:

window.onload = function() {
	try {
		var e = document.getElementById('canvas');
		var w = 640;
		var h = 480;
		var paper = Raphael(e, w, h);
		var g = new Graph();
		var va = new Vertex('aaaaa'); g.vertices.push(va);
		var vb = new Vertex('bb bb'); g.vertices.push(vb);
		var vc = new Vertex('c c c'); g.vertices.push(vc);
		var vd = new Vertex('ddddd'); g.vertices.push(vd);
		var ve = new Vertex('ee\nee'); g.vertices.push(ve);
		var vf = new Vertex('f\nf\nf'); g.vertices.push(vf);
		var eac = new Edge(va, vc); g.edges.push(eac);
		var ebc = new Edge(vb, vc); g.edges.push(ebc);
		var ecd = new Edge(vc, vd); g.edges.push(ecd);
		var ede = new Edge(vd, ve); g.edges.push(ede);
		var eae = new Edge(va, ve); g.edges.push(eae);
		var eef = new Edge(ve, vf); g.edges.push(eef);
		g.render(paper, 50, 50);
	} catch(e) {
		alert(e);
		throw e;
	}
}
